INSERT INTO `tbl_categories` VALUES ('1', 'Tin tức', 'tin-tuc', '1', '', '1463843026', '1', null, null, 'Cập nhật tin tức mới nhất, tin tức mới nhất trong và ngoài nước, tình hình thời sự, chính trị, kinh tế, giáo dục .....', 'tin tức mới nhất, tin nhanh, tin trong nước, tin quốc tế, tin khoa học, tin xã hội, tin chính trị, tin giáo dục, Cập nhật tin tức bốn phương', 'Cập nhật tin tức mới nhất, tin tức mới nhất trong và ngoài nước, tình hình thời sự, chính trị, kinh tế, giáo dục .....', null, '1', null, '0');
INSERT INTO `tbl_categories` VALUES ('2', 'Trong nước', 'trong-nuoc', '1', '', '1463843237', '1', null, null, 'Cập nhật tin tức trong nước mới nhất, cập nhật tin tức trong nước nhanh chóng, tất tần tật về tình hình trong nước : kinh tế, chính trị, ngoại giao, giáo dục, khoa học, đời sống...', 'trong nước, tin tức Việt Name, tin tức trong nước, tin nhanh Việt Nam, cập nhật tin tức trong nước, tôi yêu Việt Nam', 'Cập nhật tin tức trong nước mới nhất, cập nhật tin tức trong nước nhanh chóng, tất tần tật về tình hình trong nước : kinh tế, chính trị, ngoại giao, giáo dục, khoa học, đời sống...', null, '1', null, '1');
INSERT INTO `tbl_categories` VALUES ('3', 'Thế giới', 'the-gioi', '2', '', '1463843420', '1', null, null, 'Tin tức thế giới, cập nhật tình hình kinh tế, chính trị, xã hội, khoa học .. của thế giới, cập nhật tin thế giới nhanh nhất.', 'tin thế giới, tin nhanh thế giới, cập nhật tin thế giới nhanh nhất, tất tần tật về tin tức thế giới, tình hình kinh tế - chính trị - xã hội trên thới  giới', 'Tin tức thế giới, cập nhật tình hình kinh tế, chính trị, xã hội, khoa học .. của thế giới, cập nhật tin thế giới nhanh nhất.', null, '1', null, '1');
INSERT INTO `tbl_categories` VALUES ('4', 'Thể thao', 'the-thao', '2', '', '1463863038', '1', null, null, 'Danh mục thể thao - Cập nhật tin tức thể thao nhanh nhất', 'Tin thể thao, tin bóng đá, cập nhật tin thể thao nhanh nhất, tin tức thể thao trong và ngoài nước', 'Danh mục thể thao số cập nhật tin thể thao nhanh nhất từ các báo thể thao uy tín thế giới. Danh mục thể thao là trang tin thể thao được nhiều người xem nhất', null, '1', null, '0');
INSERT INTO `tbl_categories` VALUES ('5', 'Bóng đá Việt Nam', 'bong-da-viet-nam', '1', '', '1463863312', '1', null, null, 'Danh mục bóng đá Việt Nam', 'tin bóng đá việt nam, giải vô dịch quốc gia, V-League, tin tức bóng đá trong nước, tin chuyển nhượng  trong nước, tình hình bóng đá nước nhà', 'Cập nhật nhanh nhất tin tưc bóng đá Việt Nam, tin tức V-League, tin tức các giải bóng đá trong nước, tin chuyển nhượng, bản xếp hạng trong nước ...', null, '1', null, '4');
INSERT INTO `tbl_categories` VALUES ('6', 'Bóng đá Quốc tế', 'bong-da-quoc-te', '2', '', '1463863550', '1', null, null, 'Danh muc Bóng đá Quốc tế - Tin tức bóng đá quốc tế nhanh nhất', 'bóng đá quốc tế, tình hình báo đá quốc tế, thị trường chuyển nhượng , tin tức về các câu lạc bộ nổi tiếng, giải c1, c2 ....', 'Cập nhật nhanh nhất tin tức bóng đá quốc tế, tình hình bóng đá quốc tế, thị trường chuyển nhượng, tin tức về các câu lạc bộ nổi tiếng thế giớ, các giải vô địch quốc gia của các nước ....', null, '1', null, '4');
INSERT INTO `tbl_categories` VALUES ('7', 'Quần vợt', 'quan-vot', '3', '', '1463863766', '1', null, null, 'Cập nhật nhanh nhất tin tức quần vợt trong và ngoài nước, các  giải quần vợt trên thế giới, tình hình quần vợt thế giới', 'quần vợt trong nước, quần vợt quốc tế, giải quần vợt , tình hình quần vợt quốc tế, môn thể thao quần vợt', 'Cập nhật  nhanh nhất tin tức quần vợt trong và ngoài nước, các  giải quần vợt trên thế giới, tình hình quần vợt thế giới, tình hình các giải đấu', null, '1', null, '4');
INSERT INTO `tbl_categories` VALUES ('8', 'Các môn thể thao khác', 'cac-mon-the-thao-khac', '4', '', '1463863919', '1', null, null, 'Cập nhật tin tức về các môn như cầu lông, đua xe, đua ngựa, đấu kiếm, quyền anh, bóng bàn', 'Cập nhật tin tức về các môn như cầu lông, đua xe, đua ngựa, đấu kiếm, quyền anh, bóng bàn', 'Cập nhật tin tức về các môn như cầu lông, đua xe, đua ngựa, đấu kiếm, quyền anh, bóng bàn', null, '1', null, '4');

