INSERT INTO `tbl_logs` VALUES ('159', 'backend', 'category', 'add', '1', '1', '1463843026', '{\"cate_name\":\"Tin t\\u1ee9c\",\"cate_icon\":\"\",\"cate_slug\":\"tin-tuc\",\"cate_meta_title\":\"C\\u1eadp nh\\u1eadt tin t\\u1ee9c m\\u1edbi nh\\u1ea5t, tin t\\u1ee9c m\\u1edbi nh\\u1ea5t trong v\\u00e0 ngo\\u00e0i n\\u01b0\\u1edbc, t\\u00ecnh h\\u00ecnh th\\u1eddi s\\u1ef1, ch\\u00ednh tr\\u1ecb, kinh t\\u1ebf, gi\\u00e1o d\\u1ee5c .....\",\"cate_meta_description\":\"C\\u1eadp nh\\u1eadt tin t\\u1ee9c m\\u1edbi nh\\u1ea5t, tin t\\u1ee9c m\\u1edbi nh\\u1ea5t trong v\\u00e0 ngo\\u00e0i n\\u01b0\\u1edbc, t\\u00ecnh h\\u00ecnh th\\u1eddi s\\u1ef1, ch\\u00ednh tr\\u1ecb, kinh t\\u1ebf, gi\\u00e1o d\\u1ee5c .....\",\"cate_meta_keyword\":\"tin t\\u1ee9c m\\u1edbi nh\\u1ea5t, tin nhanh, tin trong n\\u01b0\\u1edbc, tin qu\\u1ed1c t\\u1ebf, tin khoa h\\u1ecdc, tin x\\u00e3 h\\u1ed9i, tin ch\\u00ednh tr\\u1ecb, tin gi\\u00e1o d\\u1ee5c, C\\u1eadp nh\\u1eadt tin t\\u1ee9c b\\u1ed1n ph\\u01b0\\u01a1ng\",\"cate_sort\":1,\"cate_status\":\"1\",\"created_date\":1463843026,\"user_created\":1,\"parent_id\":0}');
