INSERT INTO `tbl_logs` VALUES ('161', 'backend', 'category', 'add', '3', '1', '1463843420', '{\"cate_name\":\"Th\\u1ebf gi\\u1edbi\",\"cate_icon\":\"\",\"cate_slug\":\"the-gioi\",\"cate_meta_title\":\"Tin t\\u1ee9c th\\u1ebf gi\\u1edbi, c\\u1eadp nh\\u1eadt t\\u00ecnh h\\u00ecnh kinh t\\u1ebf, ch\\u00ednh tr\\u1ecb, x\\u00e3 h\\u1ed9i, khoa h\\u1ecdc .. c\\u1ee7a th\\u1ebf gi\\u1edbi, c\\u1eadp nh\\u1eadt tin th\\u1ebf gi\\u1edbi nhanh nh\\u1ea5t.\",\"cate_meta_description\":\"Tin t\\u1ee9c th\\u1ebf gi\\u1edbi, c\\u1eadp nh\\u1eadt t\\u00ecnh h\\u00ecnh kinh t\\u1ebf, ch\\u00ednh tr\\u1ecb, x\\u00e3 h\\u1ed9i, khoa h\\u1ecdc .. c\\u1ee7a th\\u1ebf gi\\u1edbi, c\\u1eadp nh\\u1eadt tin th\\u1ebf gi\\u1edbi nhanh nh\\u1ea5t.\",\"cate_meta_keyword\":\"tin th\\u1ebf gi\\u1edbi, tin nhanh th\\u1ebf gi\\u1edbi, c\\u1eadp nh\\u1eadt tin th\\u1ebf gi\\u1edbi nhanh nh\\u1ea5t, t\\u1ea5t t\\u1ea7n t\\u1eadt v\\u1ec1 tin t\\u1ee9c th\\u1ebf gi\\u1edbi, t\\u00ecnh h\\u00ecnh kinh t\\u1ebf - ch\\u00ednh tr\\u1ecb - x\\u00e3 h\\u1ed9i tr\\u00ean th\\u1edbi  gi\\u1edbi\",\"cate_sort\":2,\"cate_status\":\"1\",\"created_date\":1463843420,\"user_created\":1,\"parent_id\":1}');
INSERT INTO `tbl_logs` VALUES ('162', 'backend', 'category', 'add', '4', '1', '1463863038', '{\"cate_name\":\"Th\\u1ec3 thao\",\"cate_icon\":\"\",\"cate_slug\":\"the-thao\",\"cate_meta_title\":\"Danh m\\u1ee5c th\\u1ec3 thao - C\\u1eadp nh\\u1eadt tin t\\u1ee9c th\\u1ec3 thao nhanh nh\\u1ea5t\",\"cate_meta_description\":\"Danh m\\u1ee5c th\\u1ec3 thao s\\u1ed1 c\\u1eadp nh\\u1eadt tin th\\u1ec3 thao nhanh nh\\u1ea5t t\\u1eeb c\\u00e1c b\\u00e1o th\\u1ec3 thao uy t\\u00edn th\\u1ebf gi\\u1edbi. Danh m\\u1ee5c th\\u1ec3 thao l\\u00e0 trang tin th\\u1ec3 thao \\u0111\\u01b0\\u1ee3c nhi\\u1ec1u ng\\u01b0\\u1eddi xem nh\\u1ea5t\",\"cate_meta_keyword\":\"Tin th\\u1ec3 thao, tin b\\u00f3ng \\u0111\\u00e1, c\\u1eadp nh\\u1eadt tin th\\u1ec3 thao nhanh nh\\u1ea5t, tin t\\u1ee9c th\\u1ec3 thao trong v\\u00e0 ngo\\u00e0i n\\u01b0\\u1edbc\",\"cate_sort\":2,\"cate_status\":\"1\",\"created_date\":1463863038,\"user_created\":1,\"parent_id\":0}');
