INSERT INTO `tbl_logs` VALUES ('165', 'backend', 'category', 'add', '7', '1', '1463863766', '{\"cate_name\":\"Qu\\u1ea7n v\\u1ee3t\",\"cate_icon\":\"\",\"cate_slug\":\"quan-vot\",\"cate_meta_title\":\"C\\u1eadp nh\\u1eadt nhanh nh\\u1ea5t tin t\\u1ee9c qu\\u1ea7n v\\u1ee3t trong v\\u00e0 ngo\\u00e0i n\\u01b0\\u1edbc, c\\u00e1c  gi\\u1ea3i qu\\u1ea7n v\\u1ee3t tr\\u00ean th\\u1ebf gi\\u1edbi, t\\u00ecnh h\\u00ecnh qu\\u1ea7n v\\u1ee3t th\\u1ebf gi\\u1edbi\",\"cate_meta_description\":\"C\\u1eadp nh\\u1eadt  nhanh nh\\u1ea5t tin t\\u1ee9c qu\\u1ea7n v\\u1ee3t trong v\\u00e0 ngo\\u00e0i n\\u01b0\\u1edbc, c\\u00e1c  gi\\u1ea3i qu\\u1ea7n v\\u1ee3t tr\\u00ean th\\u1ebf gi\\u1edbi, t\\u00ecnh h\\u00ecnh qu\\u1ea7n v\\u1ee3t th\\u1ebf gi\\u1edbi, t\\u00ecnh h\\u00ecnh c\\u00e1c gi\\u1ea3i \\u0111\\u1ea5u\",\"cate_meta_keyword\":\"qu\\u1ea7n v\\u1ee3t trong n\\u01b0\\u1edbc, qu\\u1ea7n v\\u1ee3t qu\\u1ed1c t\\u1ebf, gi\\u1ea3i qu\\u1ea7n v\\u1ee3t , t\\u00ecnh h\\u00ecnh qu\\u1ea7n v\\u1ee3t qu\\u1ed1c t\\u1ebf, m\\u00f4n th\\u1ec3 thao qu\\u1ea7n v\\u1ee3t\",\"cate_sort\":3,\"cate_status\":\"1\",\"created_date\":1463863766,\"user_created\":1,\"parent_id\":4}');
